package collinear;public class FastCollinearPoints {    private static final int MINIMUM_LENGTH = 4;    /**     * Constructor of FastCollinearPoints.     */    public FastCollinearPoints(Point[] points) {        if (points == null) {            throw new IllegalArgumentException("The points can't be null.");        }        for (Point p : points) {            if (p == null) {                throw new IllegalArgumentException("Every point can't be null.");            }        }        int length = points.length;        if (length < MINIMUM_LENGTH) {            return;        }        for (int i = 0; i < length; i++) {            for (int j = i + 1; j < length; j++) {                if (points[i] == points[j]) {                    throw new IllegalArgumentException("Points can't repeat.");                }            }        }    }}